import React, {useState} from 'react';
import Square from './Square'
import './../App.css';



function generateRow(array){
    var row = []
    for (let i = 0; i < 9; i++){
        if (array[i] != 0){
            row.push(<Square value={array[i]}/>);
        } else {
            row.push(<Square value=' '/>);
        }

    }
    return row;
}

function generateGrid(input2DArr){
    var grid = [];
    for (let i = 0; i < 9; i++){
        var row = generateRow(input2DArr[i])
        grid.push(<li>{row}</li>);
    }
    return grid;
}

function Board(props) {
  let [grid2, setGrid] = useState(Array(9).fill(Array(9).fill(0)))

  let grid = generateGrid(grid2);  

  return (
    <div className="board">
        <ul>{grid}</ul>
    </div>
  );
}

export default Board;
